INSERT INTO `carousel` (`carousel_id`, `carousel_name`, `carousel_image`) VALUES
(1, '“Muốn khóc, không khóc được, muốn cười, không cười được. Thì ra, đau đớn đến cực điểm, chính là tê dại, là chết lặng.”', 'hacanhnoianh-carosel.png'),
(2, '“Kiếp này ta bảo vệ nàng đã đủ rồi. Sang kiếp sau đổi lại là nàng bảo vệ ta có được không?“', 'thien-co-quyet-tran-carosel.jpg'),
(3, '“Tai trái gần với tim nhất, lời nói ngọt ngào phải nói vào tai trái. Yêu đúng là tình yêu, yêu sai là tuổi trẻ.”', 'josee-carosel.jpeg');
